import React, { Component } from 'react';
import './onHand.css';
import ChatStore from './ChatStore';
import DevTools from 'mobx-react-devtools';
import { observer } from 'mobx-react';

class App extends Component {
  constructor() {
    super();
    this.state = {
      questionArea: '',
      isStandBy: false,
      audio: new Audio(
        'data:audio/wav;base64,'
      )
    };

    this.questionArea = this.questionArea.bind(this);
    this.addUserMessage = this.addUserMessage.bind(this);
  }

  questionArea(event) {
    let newText = event.target.value;

    newText.length < 240
      ? this.setState({ questionArea: newText })
      : this.state.audio.play();
  }

  addUserMessage(event) {
    const questionAreaMessage = this.state.questionArea.trim();
    if (event.keyCode === 13) {
      event.preventDefault();
      if (questionAreaMessage.trim().length > 0) {
        //1%2=>1   le va al usuario
        this.setState({ questionArea: '' });
        this.props.ChatStore.addUserMessage(questionAreaMessage);
      }
    }

    if (event === 'img') {
      if (questionAreaMessage.trim().length > 0) {
        //1%2=>1   le va al usuario
        this.setState({ questionArea: '' });
        this.props.ChatStore.addUserMessage(questionAreaMessage);
      }
    }
  }

  componentDidUpdate() {
    document.getElementsByClassName('coconutt-chat-chat')[
      0
    ].scrollTop = 2147483637;
  }

  render() {
    const ChatStore = this.props.ChatStore;

    if (this.state.isStandBy)
      return (
        <div className="coconutt-chat-standby">
          <img
            src="https://www.bomgar.com/assets/images/blog/app_full_proxy.png"
            onClick={() => {
              this.setState({ isStandBy: !this.state.isStandBy });
            }}
          />
        </div>
      );
    else
      return (
        <div>
          <div className="coconutt-chat-menu">
            <div className="coconutt-chat-back">
              <img
                src={ChatStore.chat.agentAvatar}
                draggable="false"
                alt={ChatStore.chat.agentName}
                onClick={() => {
                  this.setState({ isStandBy: !this.state.isStandBy });
                }}
              />
            </div>
            <div className="coconutt-chat-name">
              {ChatStore.chat.agentName}
            </div>
          </div>
          <div className="coconutt-chat-chat">
            <ol>
              {ChatStore.chat.messages.map(message => {
                return (
                  <li
                    autoFocus
                    key={message.id}
                    className={
                      message.id % 2 === 1
                        ? 'coconutt-chat-user'
                        : 'coconutt-chat-agent'
                    }
                  >
                    <div className="coconutt-chat-msg">
                      {message.lines.map((line, k) => {
                        return <p key={k}>{line}</p>;
                      })}
                      <time>{message.time}</time>
                    </div>
                  </li>
                );
              })}

            </ol>
          </div>
          <div className="coconutt-chat-textarea">
            <textarea
              onChange={this.questionArea}
              onKeyDown={this.addUserMessage}
              value={this.state.questionArea}
              minLength="1"
              maxLength="240"
              wrap="on"
              rows="2"
              placeholder="Â¿Cual es su pregunta?"
            />
            <img
              src="http://www.myiconfinder.com/uploads/iconsets/a3fa321a44e798b62853d2259ee99b80-Send.png"
              onClick={this.addUserMessage.bind(this, 'img')}
            />
          </div>
        </div>
      );
  }
}

export default observer(App);
